import React, { useState } from 'react';


const FormContainer = props => {
  const { DateTime } = require('luxon');

  const [getInput, setInput] = useState({
    origin: DateTime.local().hour + ':' + DateTime.local().minute,
    target: DateTime.local().zoneName
  })

  const onChange = (event) => {
    setInput({...getInput, [event.currentTarget.id]: event.currentTarget.value})
    console.log(event.currentTarget.id + ": " + event.currentTarget.value)
    }
  let rezone = ''
  const onSubmit = event => {
    event.preventDefault()
    rezone = DateTime.local().setZone(getInput.target);
    console.log(rezone)
  }

  document.getElementById('output').innerHtlm = rezone

  return(
    <div>
      <h2>
        Please Enter a Time and a Timezone!
      </h2>
      <form onSubmit={onSubmit}>
        <label>Origin:
          <br></br>
          <input
            type="text"
            id="origin"
            value={getInput.origin}
            onChange={onChange}
          />
        </label>
        <br></br>
        <br></br>
        <label>Target:
          <br></br>
          <input
            type="text"
            id="target"
            value={getInput.target}
            onChange={onChange}
          />
        </label>
          <br></br>
          <br></br>
        <input type="submit" value="Submit" />
      </form>
      <div id='output'>

      </div>
  </div>
  );
}

export default FormContainer
