import React, { useState } from 'react';
import logo from './mintbean.png';
import './styles/index.css';
import FormContainer from "./FormContainer"

const App = () => {
  // https://sites.google.com/mintbean.io/2020-09-21-timezone-converter/home
  const [getInput, setInput] = useState({
    origin: '',
    target: ''
  })

  const { DateTime } = require('luxon');
  console.log(getInput)

  // Required
  // As a user, I can enter my time & timezone into an "Origin" field.
  // let time = getInput.time


  // As a user, I can enter a target timezone into a "Target" field.
// let target = getInput.target

  //
  // If both fields are populated, the current time is converted correctly into the target time, and is displayed.
  //
  // If both fields are NOT populated, then the target time says "Please select an origin and a target time."
  //
  // As a user, my timezone is auto-detected and entered into "Origin" for me.

  // Bonus Features
  // As a user, I can convert multiple origin times into multiple target times.
  //
  // As a user, I can show the positive or negative time difference between the origin time and the target time.
  //
  // Be creative and add features not listed here.

  return (
    <div className="App">
        <FormContainer
          setTime={setInput}
          getTime={getInput}
        />
    </div>
  );
}

export default App;
